@import url('https://fonts.googleapis.com/css2?family=IBM+Plex+Sans:wght@300;400;500;600;700&family=IBM+Plex+Serif:wght@400;600&display=swap');
@import "tailwindcss";

@plugin "tailwindcss-animate";

@custom-variant dark (&:is(.dark *));

@theme {
  --font-sans: 'IBM Plex Sans', sans-serif;
  --font-serif: 'IBM Plex Serif', serif;

  --color-border: hsl(var(--border));
  --color-input: hsl(var(--input));
  --color-ring: hsl(var(--ring));
  --color-background: hsl(var(--background));
  --color-foreground: hsl(var(--foreground));

  --color-primary: hsl(var(--primary));
  --color-primary-foreground: hsl(var(--primary-foreground));

  --color-secondary: hsl(var(--secondary));
  --color-secondary-foreground: hsl(var(--secondary-foreground));

  --color-destructive: hsl(var(--destructive));
  --color-destructive-foreground: hsl(var(--destructive-foreground));

  --color-muted: hsl(var(--muted));
  --color-muted-foreground: hsl(var(--muted-foreground));

  --color-accent: hsl(var(--accent));
  --color-accent-foreground: hsl(var(--accent-foreground));

  --color-popover: hsl(var(--popover));
  --color-popover-foreground: hsl(var(--popover-foreground));

  --color-card: hsl(var(--card));
  --color-card-foreground: hsl(var(--card-foreground));

  --color-sidebar: hsl(var(--sidebar));
  --color-sidebar-foreground: hsl(var(--sidebar-foreground));
  --color-sidebar-border: hsl(var(--sidebar-border));
  --color-sidebar-accent: hsl(var(--sidebar-accent));
  --color-sidebar-accent-foreground: hsl(var(--sidebar-accent-foreground));
  --color-sidebar-muted-foreground: hsl(var(--sidebar-muted-foreground));
  --color-sidebar-ring: hsl(var(--sidebar-ring));

  --radius-lg: var(--radius);
  --radius-md: calc(var(--radius) - 2px);
  --radius-sm: calc(var(--radius) - 4px);
}

:root {
  /* Warm off-white base */
  --background: 40 20% 97%;
  --foreground: 222 30% 12%;

  --card: 0 0% 100%;
  --card-foreground: 222 30% 12%;

  --popover: 0 0% 100%;
  --popover-foreground: 222 30% 12%;

  /* Deep teal-blue primary ‚Äî academic, trustworthy */
  --primary: 213 72% 30%;
  --primary-foreground: 0 0% 100%;
  --primary-glow: 213 72% 45%;
  --primary-light: 213 60% 94%;

  --secondary: 213 20% 93%;
  --secondary-foreground: 213 30% 25%;

  --muted: 213 15% 93%;
  --muted-foreground: 213 15% 50%;

  --accent: 38 90% 55%;
  --accent-foreground: 222 30% 12%;
  --accent-light: 38 90% 95%;

  --destructive: 0 72% 51%;
  --destructive-foreground: 0 0% 100%;

  --border: 213 20% 88%;
  --input: 213 20% 88%;
  --ring: 213 72% 30%;

  --radius: 0.625rem;

  /* Sidebar ‚Äî deep navy */
  --sidebar: 222 35% 14%;
  --sidebar-foreground: 213 30% 85%;
  --sidebar-border: 222 35% 20%;
  --sidebar-accent: 213 72% 30%;
  --sidebar-accent-foreground: 0 0% 100%;
  --sidebar-muted-foreground: 213 20% 55%;
  --sidebar-ring: 213 72% 45%;

  /* Custom tokens */
  --gradient-hero: linear-gradient(135deg, hsl(213 72% 30%), hsl(222 60% 22%));
  --gradient-accent: linear-gradient(135deg, hsl(38 90% 55%), hsl(38 90% 45%));
  --gradient-card: linear-gradient(180deg, hsl(0 0% 100%), hsl(213 15% 98%));
  --shadow-elegant: 0 4px 20px -4px hsl(213 72% 30% / 0.2);
  --shadow-card: 0 2px 8px -2px hsl(213 30% 20% / 0.08);
  --shadow-glow: 0 8px 32px hsl(213 72% 30% / 0.25);
  --transition-smooth: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);
}

* {
  border-color: hsl(var(--border));
}

body {
  background-color: hsl(var(--background));
  color: hsl(var(--foreground));
  font-family: var(--font-sans);
  -webkit-font-smoothing: antialiased;
}

/* Stat card variants */
.stat-card-primary {
  background: var(--gradient-hero);
  color: hsl(0 0% 100%);
  box-shadow: var(--shadow-glow);
}

.stat-card-accent {
  background: var(--gradient-accent);
  color: hsl(222 30% 12%);
  box-shadow: 0 8px 24px hsl(38 90% 55% / 0.3);
}

.stat-card-default {
  background: hsl(var(--card));
  box-shadow: var(--shadow-card);
  border: 1px solid hsl(var(--border));
}

/* Hero section */
.dashboard-hero {
  background: var(--gradient-hero);
  border-radius: var(--radius);
  padding: 2.5rem;
  color: hsl(0 0% 100%);
  box-shadow: var(--shadow-glow);
  position: relative;
  overflow: hidden;
}

.dashboard-hero::before {
  content: '';
  position: absolute;
  top: -50%;
  right: -10%;
  width: 60%;
  height: 200%;
  background: radial-gradient(ellipse, hsl(213 72% 50% / 0.25) 0%, transparent 70%);
  pointer-events: none;
}

/* Chart card */
.chart-card {
  background: hsl(var(--card));
  border: 1px solid hsl(var(--border));
  border-radius: var(--radius);
  box-shadow: var(--shadow-card);
}

/* Badge variants */
.badge-paid {
  background: hsl(142 60% 90%);
  color: hsl(142 70% 25%);
  border: 1px solid hsl(142 50% 75%);
}

.badge-unpaid {
  background: hsl(0 60% 93%);
  color: hsl(0 70% 40%);
  border: 1px solid hsl(0 50% 80%);
}

