@import url('https://fonts.googleapis.com/css2?family=IBM+Plex+Sans:wght@300;400;500;600;700&family=IBM+Plex+Serif:ital,wght@0,400;0,600;1,400&display=swap');
@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

@theme inline {
  --font-sans: 'IBM Plex Sans', sans-serif;
  --font-serif: 'IBM Plex Serif', serif;

  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);
  --color-sidebar-muted-foreground: var(--sidebar-muted-foreground);
}

:root {
  --radius: 0.5rem;

  /* Warm off-white base */
  --background: oklch(0.978 0.003 247);
  --foreground: oklch(0.18 0.025 250);

  --card: oklch(1 0 0);
  --card-foreground: oklch(0.18 0.025 250);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.18 0.025 250);

  /* Deep navy-teal primary */
  --primary: oklch(0.38 0.1 240);
  --primary-foreground: oklch(0.985 0 0);

  --secondary: oklch(0.94 0.01 245);
  --secondary-foreground: oklch(0.28 0.05 245);

  --muted: oklch(0.94 0.008 245);
  --muted-foreground: oklch(0.52 0.025 245);

  /* Warm amber accent */
  --accent: oklch(0.78 0.13 75);
  --accent-foreground: oklch(0.15 0.03 75);

  --destructive: oklch(0.577 0.245 27.325);
  --border: oklch(0.9 0.012 245);
  --input: oklch(0.9 0.012 245);
  --ring: oklch(0.38 0.1 240);

  --chart-1: oklch(0.38 0.1 240);
  --chart-2: oklch(0.78 0.13 75);
  --chart-3: oklch(0.55 0.12 175);
  --chart-4: oklch(0.65 0.14 310);
  --chart-5: oklch(0.60 0.16 40);

  /* Sidebar ‚Äî deep navy */
  --sidebar: oklch(0.20 0.04 248);
  --sidebar-foreground: oklch(0.80 0.025 245);
  --sidebar-primary: oklch(0.50 0.12 240);
  --sidebar-primary-foreground: oklch(0.985 0 0);
  --sidebar-accent: oklch(0.28 0.05 248);
  --sidebar-accent-foreground: oklch(0.92 0.015 245);
  --sidebar-border: oklch(0.26 0.04 248);
  --sidebar-ring: oklch(0.50 0.12 240);
  --sidebar-muted-foreground: oklch(0.55 0.025 248);

  /* Custom design tokens */
  --gradient-hero: linear-gradient(135deg, oklch(0.34 0.11 240), oklch(0.22 0.05 248));
  --gradient-accent: linear-gradient(135deg, oklch(0.80 0.14 75), oklch(0.72 0.16 65));
  --gradient-teal: linear-gradient(135deg, oklch(0.48 0.12 185), oklch(0.38 0.10 200));
  --shadow-elegant: 0 4px 24px -4px oklch(0.18 0.05 240 / 0.18);
  --shadow-card: 0 2px 8px -2px oklch(0.18 0.05 240 / 0.08);
  --shadow-hero: 0 12px 40px oklch(0.22 0.08 240 / 0.30);
  --transition-smooth: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
    font-family: 'IBM Plex Sans', sans-serif;
    -webkit-font-smoothing: antialiased;
  }
}

/* ===== DASHBOARD HERO ===== */
.dashboard-hero {
  background: var(--gradient-hero);
  border-radius: var(--radius);
  color: oklch(0.985 0 0);
  box-shadow: var(--shadow-hero);
  position: relative;
  overflow: hidden;
}

.dashboard-hero::before {
  content: '';
  position: absolute;
  top: -40%;
  right: -5%;
  width: 55%;
  height: 200%;
  background: radial-gradient(ellipse, oklch(0.55 0.12 240 / 0.3) 0%, transparent 65%);
  pointer-events: none;
}

.dashboard-hero::after {
  content: '';
  position: absolute;
  bottom: -20%;
  left: 5%;
  width: 40%;
  height: 150%;
  background: radial-gradient(ellipse, oklch(0.78 0.13 75 / 0.12) 0%, transparent 65%);
  pointer-events: none;
}

/* ===== STAT CARD VARIANTS ===== */
.kpi-primary {
  background: var(--gradient-hero);
  color: oklch(0.985 0 0);
  box-shadow: var(--shadow-hero);
  border: none;
}

.kpi-accent {
  background: var(--gradient-accent);
  color: oklch(0.15 0.03 75);
  box-shadow: 0 8px 28px oklch(0.78 0.13 75 / 0.35);
  border: none;
}

.kpi-teal {
  background: var(--gradient-teal);
  color: oklch(0.985 0 0);
  box-shadow: 0 8px 28px oklch(0.38 0.10 185 / 0.30);
  border: none;
}

.kpi-default {
  background: oklch(1 0 0);
  box-shadow: var(--shadow-card);
  border: 1px solid oklch(0.9 0.012 245);
}

/* ===== CHART WRAPPER ===== */
.chart-panel {
  background: oklch(1 0 0);
  border: 1px solid oklch(0.9 0.012 245);
  border-radius: var(--radius);
  box-shadow: var(--shadow-card);
}

