  --chart-5: oklch(0.60 0.16 40);

  /* Sidebar ‚Äî deep navy */
  --sidebar: oklch(0.20 0.04 248);
  --sidebar-foreground: oklch(0.80 0.025 245);
  --sidebar-primary: oklch(0.50 0.12 240);
  --sidebar-primary-foreground: oklch(0.985 0 0);
  --sidebar-accent: oklch(0.28 0.05 248);
  --sidebar-accent-foreground: oklch(0.92 0.015 245);
  --sidebar-border: oklch(0.26 0.04 248);
  --sidebar-ring: oklch(0.50 0.12 240);
  --sidebar-muted-foreground: oklch(0.55 0.025 248);

  /* Custom design tokens */
  --gradient-hero: linear-gradient(135deg, oklch(0.34 0.11 240), oklch(0.22 0.05 248));
  --gradient-accent: linear-gradient(135deg, oklch(0.80 0.14 75), oklch(0.72 0.16 65));
  --gradient-teal: linear-gradient(135deg, oklch(0.48 0.12 185), oklch(0.38 0.10 200));
  --shadow-elegant: 0 4px 24px -4px oklch(0.18 0.05 240 / 0.18);
  --shadow-card: 0 2px 8px -2px oklch(0.18 0.05 240 / 0.08);
  --shadow-hero: 0 12px 40px oklch(0.22 0.08 240 / 0.30);
  --transition-smooth: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
    font-family: 'IBM Plex Sans', sans-serif;
    -webkit-font-smoothing: antialiased;
  }
}

/* ===== DASHBOARD HERO ===== */
.dashboard-hero {
  background: var(--gradient-hero);
  border-radius: var(--radius);
  color: oklch(0.985 0 0);
  box-shadow: var(--shadow-hero);
  position: relative;
  overflow: hidden;
}

.dashboard-hero::before {
  content: '';
  position: absolute;
  top: -40%;
  right: -5%;
  width: 55%;
  height: 200%;
  background: radial-gradient(ellipse, oklch(0.55 0.12 240 / 0.3) 0%, transparent 65%);
  pointer-events: none;
}

.dashboard-hero::after {
  content: '';
  position: absolute;
  bottom: -20%;
  left: 5%;
  width: 40%;
  height: 150%;
  background: radial-gradient(ellipse, oklch(0.78 0.13 75 / 0.12) 0%, transparent 65%);
  pointer-events: none;
}

/* ===== STAT CARD VARIANTS ===== */
.kpi-primary {
  background: var(--gradient-hero);
  color: oklch(0.985 0 0);
  box-shadow: var(--shadow-hero);
  border: none;
}

.kpi-accent {
  background: var(--gradient-accent);
  color: oklch(0.15 0.03 75);
  box-shadow: 0 8px 28px oklch(0.78 0.13 75 / 0.35);
  border: none;
}

.kpi-teal {
  background: var(--gradient-teal);
  color: oklch(0.985 0 0);
  box-shadow: 0 8px 28px oklch(0.38 0.10 185 / 0.30);
  border: none;
}

.kpi-default {
  background: oklch(1 0 0);
  box-shadow: var(--shadow-card);
  border: 1px solid oklch(0.9 0.012 245);
}

/* ===== CHART WRAPPER ===== */
.chart-panel {
  background: oklch(1 0 0);
  border: 1px solid oklch(0.9 0.012 245);
  border-radius: var(--radius);
  box-shadow: var(--shadow-card);
}

